import React, { Component } from "react";
import { w3cwebsocket as W3CWebSocket } from "websocket";
import Identicon from "react-identicons";
import { Navbar, NavbarBrand, UncontrolledTooltip } from "reactstrap";
import Editor from "react-medium-editor";
import "medium-editor/dist/css/medium-editor.css";
import "medium-editor/dist/css/themes/default.css";
import "./App.css";
import SampleVideo from "./sample-mp4-file.mp4";
import captions from "./sample.vtt";

const client = new W3CWebSocket("ws://127.0.0.1:8000");
const contentDefaultMessage = "Start writing your document here";
const contentDefaultMessageVideo = "Enter Video TimeStamp";

class App extends Component {
  constructor(props) {
    super(props);
    this.myRef = React.createRef();
    this.state = {
      currentUsers: [],
      userActivity: [],
      username: null,
      text: "",
      videoTimeStamp: "",
      video: SampleVideo,
      currentVideotime: "",
      SavedText: localStorage.getItem("myValuetext") || "",
      savedvideotime: localStorage.getItem("myValuevideoTimeStamp") || "",
      savedsubmittime: localStorage.getItem("myValueSubmitedTime") || "",
      cap: captions,
      track: "",
      fileReader: "",
    };
  }

  logInUser = () => {
    const username = this.username.value;
    if (username.trim()) {
      const data = {
        username,
      };
      this.setState(
        {
          ...data,
        },
        () => {
          client.send(
            JSON.stringify({
              ...data,
              type: "userevent",
            })
          );
        }
      );
    }
  };
  handlefileread = (e) => {
    const content = this.state.fileReader.result;
    console.log("content", content);
  };
  submitText = () => {
    // fetch(this.state.cap)
    //   .then((r) => r.text())
    //   .then((text) => {
    //     console.log("text decoded:", text);
    //   });

    localStorage.setItem("myValuetext", this.state.text);
    localStorage.setItem("myValuevideoTimeStamp", this.state.videoTimeStamp);
    localStorage.setItem("myValueSubmitedTime", new Date().getTime());

    console.log("this.state.text", this.state.text);
    console.log("this.state.videoTimeStamp", this.state.videoTimeStamp);
    console.log("current Time stamp", new Date().getTime());
  };

  addTrackTag = () => {
    let track = document.createElement("track");
    track.kind = "captions";
    track.label = "English";
    track.srclang = "en";
    track.src = this.state.cap;
    track.addEventListener("load", function () {
      this.mode = "showing";
      this.myRef.current.textTracks[0].mode = "showing";
    });
    this.appendChild(track);
  };

  createtrackTag = () => {
    console.log(" this.myRef.current", this.myRef.current);
    this.myRef.current.addTrackTag("captions", "English", "en");

    // this.myRef.current.addEventListener(
    //   "loadedmetadata",
    //   function() {
    //     // We can't dynamically load <tracks> for subtitles, so we have to hook into the onload of the video...
    //     this.state.track = document.createElement("track");
    //     this.state.track.kind = "captions";
    //     this.state.track.label = "English";
    //     this.state.track.srclang = "en";
    //     this.state.track.src = this.state.cap;
    //     this.state.track.addEventListener("load", function() {
    //       this.mode = "showing";
    //       this.myRef.current.textTracks[0].mode = "showing";
    //     });
    //     this.appendChild(this.state.track);
    //   },
    //   true
    // );

    // const boy = (this.myRef.current.textTracks[0].cues[0].text =
    //   "how are you boy");
    // console.log("dh", boy);
    //     track.addCue(new VTTCue(0, 12, "[Test]"));
    // track.addCue(new VTTCue(18.7, 21.5, "This blade has a dark past."));
    // track.addCue(new VTTCue(22.8, 26.8, "It has shed much innocent blood."));

    // this.myRef.current;
    // console.log("this.state.text", this.state.text);
    // console.log("this.state.videoTimeStamp", this.state.videoTimeStamp);
    // console.log("current Time stamp", new Date().getTime());
    // localStorage.setItem("myValuetext", this.state.text);
    // localStorage.setItem("myValuevideoTimeStamp", this.state.videoTimeStamp);
    // localStorage.setItem("myValueSubmitedTime", new Date().getTime());
  };

  /* When content changes, we send the
current content of the editor to the server. */
  onEditorStateChange = (text) => {
    console.log("text", text);
    client.send(
      JSON.stringify({
        type: "contentchange",
        username: this.state.username,
        content: text,
      })
    );
  };

  onVideoTimeStampStateChange = (time) => {
    console.log("onVideoTimeStampStateChange", time);
    client.send(
      JSON.stringify({
        type: "videotimechange",
        username: this.state.username,
        content: time,
      })
    );
  };

  componentWillMount() {
    client.onopen = () => {
      console.log("WebSocket Client Connected");
    };
    client.onmessage = (message) => {
      const dataFromServer = JSON.parse(message.data);
      const stateToChange = {};
      if (dataFromServer.type === "userevent") {
        stateToChange.currentUsers = Object.values(dataFromServer.data.users);
      } else if (dataFromServer.type === "contentchange") {
        stateToChange.text =
          dataFromServer.data.editorContent || contentDefaultMessage;
      } else if (dataFromServer.type === "videotimechange") {
        stateToChange.videoTimeStamp =
          dataFromServer.data.VideoTimeStamp || contentDefaultMessageVideo;
      }
      stateToChange.userActivity = dataFromServer.data.userActivity;
      this.setState({
        ...stateToChange,
      });
    };
    console.log("savedtext", this.state.SavedText);
    console.log("savedvideotime", this.state.savedvideotime);
    console.log("savedtexttime", this.state.savedsubmittime);
  }

  Playback = () => {
    // window.location.reload(false);
    // this.myRef.current.playbackRate;
  };

  onInputchange = (e) => {
    this.setState({ text: e.target.value });

    // client.send(
    //   JSON.stringify({
    //     type: "contentchange",
    //     username: this.state.username,
    //     content: this.state.text,
    //   })
    // );
    console.log(this.state.text);
  };

  showLoginSection = () => (
    <div className="account">
      <div className="account__wrapper">
        <div className="account__card">
          <div className="account__profile">
            <Identicon
              className="account__avatar"
              size={64}
              string="randomness"
            />
            <p className="account__name">Hello, user!</p>
            <p className="account__sub">Join the Session</p>
          </div>
          <input
            name="username"
            ref={(input) => {
              this.username = input;
            }}
            className="form-control"
          />
          <button
            type="button"
            onClick={() => this.logInUser()}
            className="btn btn-primary account__btn"
          >
            Join
          </button>
        </div>
      </div>
    </div>
  );

  showEditorSection = () => (
    <div className="main-content">
      <div className="document-holder">
        <div className="currentusers">
          {this.state.currentUsers.map((user) => (
            <React.Fragment>
              <span id={user.username} className="userInfo" key={user.username}>
                <Identicon
                  className="account__avatar"
                  style={{ backgroundColor: user.randomcolor }}
                  size={40}
                  string={user.username}
                />
              </span>
              <UncontrolledTooltip placement="top" target={user.username}>
                {user.username}
              </UncontrolledTooltip>
            </React.Fragment>
          ))}
        </div>
        {this.state.SavedText && "1" == "1" && (
          <div>{this.state.SavedText}</div>
        )}
        <video
          controls
          autoPlay={true}
          ref={this.myRef}
          onTimeUpdate={this.hello.bind(this)}
          // onLoad={this.createtrackTag()}
        >
          <source src={this.state.video} type="video/mp4" />
          <track
            label="English"
            kind="captions"
            srcLang="en"
            src={this.state.cap}
            default
          />
        </video>
        <br />
        Enter Text
        <input
          name="caption"
          type="text"
          ref="text"
          value={this.state.text}
          onChange={this.onInputchange}
        />
        {/* <Editor
          options={{
            placeholder: {
              text: this.state.text ? contentDefaultMessage : "",
            },
          }}
          className="body-editor"
          text={this.state.text}
          onChange={this.onEditorStateChange}
        /> */}
        Enter Video Time Stamp
        <input
          name="videotime"
          type="text"
          value={this.state.currentVideotime}
          // onChange={(e) => this.onInputchange(e)}
        />
        {/* <Editor
          options={{
            placeholder: {
              text: this.state.videoTimeStamp ? contentDefaultMessageVideo : "",
            },
          }}
          className="body-editor"
          text={this.state.videoTimeStamp}
          onChange={this.onVideoTimeStampStateChange}
        /> */}
        <button
          type="button"
          onClick={() => this.submitText()}
          className="btn btn-primary account__btn"
        >
          Submit
        </button>
        <button
          type="button"
          onClick={() => this.Playback()}
          className="btn btn-primary account__btn"
        >
          PlayBack
        </button>
      </div>
      <div className="history-holder">
        <ul>
          {this.state.userActivity.map((activity, index) => (
