import React, { Component } from "react";
import { w3cwebsocket as W3CWebSocket } from "websocket";
import Identicon from "react-identicons";
import { Navbar, NavbarBrand, UncontrolledTooltip } from "reactstrap";
import Editor from "react-medium-editor";
import "medium-editor/dist/css/medium-editor.css";
import "medium-editor/dist/css/themes/default.css";
import "./App.css";
import SampleVideo from "./sample-mp4-file.mp4";
import captions from "./sample.vtt";
import firebase from "./firebase";
import EditorJs from "react-editor-js";
import { EDITOR_JS_TOOLS } from "./tools";

const client = new W3CWebSocket("ws://127.0.0.1:8000");
const contentDefaultMessage = "Start writing your document here";
const contentDefaultMessageVideo = "Enter Video TimeStamp";

class App extends Component {
  constructor(props) {
    super(props);
    this.myRef = React.createRef();
    this.state = {
      currentUsers: [],
      userActivity: [],
      username: null,
      value: "",
      time: "",
      videoTimeStamp: "",
      video: SampleVideo,
      currentVideotime: "",
      SavedText: localStorage.getItem("myValuetext") || "",
      savedvideotime: localStorage.getItem("myValuevideoTimeStamp") || "",
      savedsubmittime: localStorage.getItem("myValueSubmitedTime") || "",
      details: "",
      videoName: "sample-mp4-file",
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleVideoChange = this.handleVideoChange.bind(this);
  }
  handleSubmit(event) {
    console.log("A name was submitted: " + this.state.value);
    console.log("A time was submitted: " + this.state.time);

    if (
      this.state.value &&
      this.state.time &&
      this.state.time <= parseInt(this.myRef.current.duration)
    ) {
      const db = firebase.firestore().collection("VideoCaptions");

      db.where("VideoName", "==", this.state.videoName)
        .where("StartTime", "==", Number(this.state.time))
        .get()
        .then((querySnapshot) => {
          const result = querySnapshot.docs.map((doc) => doc.data());
          const docId = querySnapshot.docs.map((doc) => doc.id)[0];

          if (result.length == 0) {
            db.add({
              Caption: this.state.value,
              StartTime: Number(this.state.time),
              EndTime: Number(this.state.time) + 3,
              VideoName: this.state.videoName,
            });
          } else {
            db.doc(docId).update({
              Caption: this.state.value,
              EndTime: Number(this.state.time) + 3,
            });
          }
        });
    }
    event.preventDefault();
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  handleVideoChange(event) {
    this.setState({ time: event.target.value });
  }

  logInUser = () => {
    const username = this.username.value;
    if (username.trim()) {
      const data = {
        username,
      };
      this.setState(
        {
          ...data,
        },
        () => {
          client.send(
            JSON.stringify({
              ...data,
              type: "userevent",
            })
          );
        }
      );
    }
  };

  loadtracks = async () => {
    var hello = this.myRef.current.addTextTrack("subtitles", "English", "en");
    hello.mode = "showing";

    const db = firebase.firestore();
    const data = await db.collection("VideoCaptions");

    const snapshot = await data.get();
    snapshot.forEach((doc) => {
      hello.addCue(
        new VTTCue(doc.data().StartTime, doc.data().EndTime, doc.data().Caption)
      );
    });
  };

  /* When content changes, we send the
current content of the editor to the server. */
  onEditorStateChange = (text) => {
    this.setState({ value: text });
    // client.send(
    //   JSON.stringify({
    //     type: "contentchange",
    //     username: this.state.username,
    //     content: text,
    //   })
    // );
  };

  componentWillMount() {
    client.onopen = () => {
      console.log("WebSocket Client Connected");
    };
    client.onmessage = (message) => {
      const dataFromServer = JSON.parse(message.data);
      const stateToChange = {};
      if (dataFromServer.type === "userevent") {
        stateToChange.currentUsers = Object.values(dataFromServer.data.users);
      } else if (dataFromServer.type === "contentchange") {
        stateToChange.text =
          dataFromServer.data.editorContent || contentDefaultMessage;
      } else if (dataFromServer.type === "videotimechange") {
        stateToChange.videoTimeStamp =
          dataFromServer.data.VideoTimeStamp || contentDefaultMessageVideo;
      }
      stateToChange.userActivity = dataFromServer.data.userActivity;
      this.setState({
        ...stateToChange,
      });
    };
  }

  showLoginSection = () => (
    <div className="account">
      <div className="account__wrapper">
        <div className="account__card">
          <div className="account__profile">
            <Identicon
              className="account__avatar"
              size={64}
              string="randomness"
            />
            <p className="account__name">Hello, user!</p>
            <p className="account__sub">Join the Session</p>
          </div>
          <input
            name="username"
            ref={(input) => {
              this.username = input;
            }}
            className="form-control"
          />
          <button
            type="button"
            onClick={() => this.logInUser()}
            className="btn btn-primary account__btn"
          >
            Join
          </button>
        </div>
      </div>
    </div>
  );

  showEditorSection = () => (
    <div className="main-content">
      <div className="document-holder">
        <div className="currentusers">
          {this.state.currentUsers.map((user) => (
            <React.Fragment>
              <span id={user.username} className="userInfo" key={user.username}>
                <Identicon
                  className="account__avatar"
                  style={{ backgroundColor: user.randomcolor }}
                  size={40}
                  string={user.username}
                />
              </span>
              <UncontrolledTooltip placement="top" target={user.username}>
                {user.username}
              </UncontrolledTooltip>
            </React.Fragment>
          ))}
        </div>
        {this.state.SavedText && "1" == "1" && (
          <div>{this.state.SavedText}</div>
        )}
        <video
          id="sample-video"
          controls
          autoPlay={true}
          ref={this.myRef}
          onLoadedMetadata={this.loadtracks}
        >
          <source src={this.state.video} type="video/mp4" />
        </video>
        <br />

        <form onSubmit={this.handleSubmit}>
          <Editor
            options={{
              placeholder: {
                text: this.state.text ? contentDefaultMessage : "",
              },
            }}
            className="body-editor"
            text={this.state.value}
            onChange={this.onEditorStateChange}
          />
          <label>
            Name:
            <input
              type="text"
              value={this.state.value}
              onChange={this.handleChange}
            />
          </label>
          <label>
            video Time:
            <input
              type="text"
              value={this.state.time}
              onChange={this.handleVideoChange}
            />
          </label>
          <input
            type="submit"
            className="btn btn-primary account__btn"
            value="Submit"
          />
        </form>
      </div>
      <div className="history-holder">
        <ul>
          {this.state.userActivity.map((activity, index) => (
            <li key={`activity-${index}`}>{activity}</li>
          ))}
        </ul>
      </div>
    </div>
  );

  render() {
    const { username } = this.state;
    return (
      <React.Fragment>
        <Navbar color="light" light>
          <NavbarBrand href="/">Virti</NavbarBrand>
        </Navbar>
        <div className="container-fluid">
          {username ? this.showEditorSection() : this.showLoginSection()}
        </div>
      </React.Fragment>
    );
  }
}

export default App;
