import "./App.css";
import React from "react";

import SampleVideo from "./sample-mp4-file.mp4";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.myRef = React.createRef();
    this.state = {
      value: "",
      video: SampleVideo,
    };
  }

  handleVideoMounted = (element) => {
    console.log("hello", element.currentTime);
  };

  hello() {
    console.log("TimeStamp", this.myRef.currentTime);
  }

  render() {
    return (
      <div>
        <video
          controls
          autoPlay={true}
          ref={this.myRef}
          onTimeUpdate={this.hello.bind(this)}
        >
          <source src={this.state.video} type="video/mp4" />
          <track
            label="English"
            kind="subtitles"
            srcLang="en"
            src="life.vtt"
            default
          />
        </video>
      </div>
    );
  }
}

export default App;
