import "./App.css";
import React from "react";

import SampleVideo from "./sample-mp4-file.mp4";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: "",
      video: SampleVideo,
    };
  }

  handleVideoMounted = (element) => {
    if (element !== null) {
      element.currentTime = 30;
    }
  };

  render() {
    return (
      <div>
        <video controls autoPlay={true} ref={this.handleVideoMounted}>
          <source src={this.state.video} type="video/mp4" />
          <track
            label="English"
            kind="subtitles"
            srcLang="en"
            src="life.vtt"
            default
          />
        </video>
      </div>
    );
  }
}

export default App;
